contract KonohaERC20 is IKonohaERC20 &#123;
    using SafeMath for uint256;

    string public constant name = "Konoha Liquidity";
    string public constant symbol = "Konoha";
    uint8 public constant decimals = 18;
    uint256 public totalSupply;
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    bytes32 public DOMAIN_SEPARATOR;
    // keccak256("Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)");
    bytes32 public constant PERMIT_TYPEHASH =
        0x6e71edae12b1b97f4d1f60370fef10105fa2faae0126114a169c64845d6126c9;
    mapping(address => uint256) public nonces;

    event Approval(
        address indexed owner,
        address indexed spender,
        uint256 value
    );
    event Transfer(address indexed from, address indexed to, uint256 value);

    constructor() public &#123;
        uint256 chainId;
        assembly &#123;
            chainId := chainid
        &#125;
        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                keccak256(
                    "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
                ),
                keccak256(bytes(name)),
                keccak256(bytes("1")),
                chainId,
                address(this)
            )
        );
    &#125;

    function _mint(address to, uint256 value) internal &#123;
        totalSupply = totalSupply.add(value);
        balanceOf[to] = balanceOf[to].add(value);
        emit Transfer(address(0), to, value);
    &#125;

    function _burn(address from, uint256 value) internal &#123;
        balanceOf[from] = balanceOf[from].sub(value);
        totalSupply = totalSupply.sub(value);
        emit Transfer(from, address(0), value);
    &#125;

    function _approve(
        address owner,
        address spender,
        uint256 value
    ) private &#123;
        allowance[owner][spender] = value;
        emit Approval(owner, spender, value);
    &#125;

    function _transfer(
        address from,
        address to,
        uint256 value
    ) private &#123;
        balanceOf[from] = balanceOf[from].sub(value);
        balanceOf[to] = balanceOf[to].add(value);
        emit Transfer(from, to, value);
    &#125;

    function approve(address spender, uint256 value) external returns (bool) &#123;
        _approve(msg.sender, spender, value);
        return true;
    &#125;

    function transfer(address to, uint256 value) external returns (bool) &#123;
        _transfer(msg.sender, to, value);
        return true;
    &#125;

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external returns (bool) &#123;
        if (allowance[from][msg.sender] != uint256(-1)) &#123;
            allowance[from][msg.sender] = allowance[from][msg.sender].sub(
                value
            );
        &#125;
        _transfer(from, to, value);
        return true;
    &#125;

    function permit(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external &#123;
        require(deadline >= block.timestamp, "Konoha: EXPIRED");
        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                DOMAIN_SEPARATOR,
                keccak256(
                    abi.encode(
                        PERMIT_TYPEHASH,
                        owner,
                        spender,
                        value,
                        nonces[owner]++,
                        deadline
                    )
                )
            )
        );
        address recoveredAddress = ecrecover(digest, v, r, s);
        require(
            recoveredAddress != address(0) &amp;&amp; recoveredAddress == owner,
            "Konoha: INVALID_SIGNATURE"
        );
        _approve(owner, spender, value);
    &#125;
&#125;

contract KonohaPair is IKonohaPair, KonohaERC20 &#123;
    using SafeMath for uint256;
    using UQ112x112 for uint224;

    uint256 public constant MINIMUM_LIQUIDITY = 10**3;
    bytes4 private constant SELECTOR =
        bytes4(keccak256(bytes("transfer(address,uint256)")));

    address public factory;
    address public token0;
    address public token1;

    uint112 public reserve0; // uses single storage slot, accessible via getReserves
    uint112 public reserve1; // uses single storage slot, accessible via getReserves
    uint32 public blockTimestampLast; // uses single storage slot, accessible via getReserves

    uint256 public price0CumulativeLast;
    uint256 public price1CumulativeLast;
    uint256 public kLast; // reserve0 * reserve1, as of immediately after the most recent liquidity event

    uint256 private unlocked = 1;
    modifier lock() &#123;
        require(unlocked == 1, "Konoha: LOCKED");
        unlocked = 0;
        _;
        unlocked = 1;
    &#125;

    function getReserves()
        public
        view
        returns (
            uint112 _reserve0,
            uint112 _reserve1,
            uint32 _blockTimestampLast
        )
    &#123;
        _reserve0 = reserve0;
        _reserve1 = reserve1;
        _blockTimestampLast = blockTimestampLast;
    &#125;

    function _safeTransfer(
        address token,
        address to,
        uint256 value
    ) private &#123;
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(SELECTOR, to, value)
        );
        require(
            success &amp;&amp; (data.length == 0 || abi.decode(data, (bool))),
            "Konoha: TRANSFER_FAILED"
        );
    &#125;

    event Mint(address indexed sender, uint256 amount0, uint256 amount1);
    event Burn(
        address indexed sender,
        uint256 amount0,
        uint256 amount1,
        address indexed to
    );
    event Swap(
        address indexed sender,
        uint256 amount0In,
        uint256 amount1In,
        uint256 amount0Out,
        uint256 amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    constructor() public &#123;
        factory = msg.sender;
    &#125;

    // called once by the factory at time of deployment
    function initialize(address _token0, address _token1) external &#123;
        require(msg.sender == factory, "Konoha: FORBIDDEN"); // sufficient check
        token0 = _token0;
        token1 = _token1;
    &#125;

    // update reserves and, on the first call per block, price accumulators
    function _update(
        uint256 balance0,
        uint256 balance1,
        uint112 _reserve0,
        uint112 _reserve1
    ) private &#123;
        require(
            balance0 &lt;= uint112(-1) &amp;&amp; balance1 &lt;= uint112(-1),
            "Konoha: OVERFLOW"
        );
        uint32 blockTimestamp = uint32(block.timestamp % 2**32);
        uint32 timeElapsed = blockTimestamp - blockTimestampLast; // overflow is desired
        if (timeElapsed > 0 &amp;&amp; _reserve0 != 0 &amp;&amp; _reserve1 != 0) &#123;
            // * never overflows, and + overflow is desired
            price0CumulativeLast +=
                uint256(UQ112x112.encode(_reserve1).uqdiv(_reserve0)) *
                timeElapsed;
            price1CumulativeLast +=
                uint256(UQ112x112.encode(_reserve0).uqdiv(_reserve1)) *
                timeElapsed;
        &#125;
        reserve0 = uint112(balance0);
        reserve1 = uint112(balance1);
        blockTimestampLast = blockTimestamp;
        emit Sync(reserve0, reserve1);
    &#125;

    // if fee is on, mint liquidity equivalent to 1/6th of the growth in sqrt(k)
    function _mintFee(uint112 _reserve0, uint112 _reserve1)
        private
        returns (bool feeOn)
    &#123;
        address feeTo = IKonohaFactory(factory).feeTo();
        feeOn = feeTo != address(0);
        uint256 _kLast = kLast; // gas savings
        if (feeOn) &#123;
            if (_kLast != 0) &#123;
                uint256 rootK = Math.sqrt(uint256(_reserve0).mul(_reserve1));
                uint256 rootKLast = Math.sqrt(_kLast);
                if (rootK > rootKLast) &#123;
                    uint256 numerator = totalSupply.mul(rootK.sub(rootKLast));
                    uint256 denominator = rootK.mul(5).add(rootKLast);
                    uint256 liquidity = numerator / denominator;
                    if (liquidity > 0) _mint(feeTo, liquidity);
                &#125;
            &#125;
        &#125; else if (_kLast != 0) &#123;
            kLast = 0;
        &#125;
    &#125;

    // this low-level function should be called from a contract which performs important safety checks
    function mint(address to) external lock returns (uint256 liquidity) &#123;
        (uint112 _reserve0, uint112 _reserve1, ) = getReserves(); // gas savings
        uint256 balance0 = IERC20(token0).balanceOf(address(this));
        uint256 balance1 = IERC20(token1).balanceOf(address(this));
        uint256 amount0 = balance0.sub(_reserve0);
        uint256 amount1 = balance1.sub(_reserve1);

        bool feeOn = _mintFee(_reserve0, _reserve1);
        uint256 _totalSupply = totalSupply; // gas savings, must be defined here since totalSupply can update in _mintFee
        if (_totalSupply == 0) &#123;
            liquidity = Math.sqrt(amount0.mul(amount1)).sub(MINIMUM_LIQUIDITY);
            _mint(address(0), MINIMUM_LIQUIDITY); // permanently lock the first MINIMUM_LIQUIDITY tokens
        &#125; else &#123;
            liquidity = Math.min(
                amount0.mul(_totalSupply) / _reserve0,
                amount1.mul(_totalSupply) / _reserve1
            );
        &#125;
        require(liquidity > 0, "Konoha: INSUFFICIENT_LIQUIDITY_MINTED");
        _mint(to, liquidity);

        _update(balance0, balance1, _reserve0, _reserve1);
        if (feeOn) kLast = uint256(reserve0).mul(reserve1); // reserve0 and reserve1 are up-to-date
        emit Mint(msg.sender, amount0, amount1);
    &#125;

    // this low-level function should be called from a contract which performs important safety checks
    function burn(address to)
        external
        lock
        returns (uint256 amount0, uint256 amount1)
    &#123;
        (uint112 _reserve0, uint112 _reserve1, ) = getReserves(); // gas savings
        address _token0 = token0; // gas savings
        address _token1 = token1; // gas savings
        uint256 balance0 = IERC20(_token0).balanceOf(address(this));
        uint256 balance1 = IERC20(_token1).balanceOf(address(this));
        uint256 liquidity = balanceOf[address(this)];

        bool feeOn = _mintFee(_reserve0, _reserve1);
        uint256 _totalSupply = totalSupply; // gas savings, must be defined here since totalSupply can update in _mintFee
        amount0 = liquidity.mul(balance0) / _totalSupply; // using balances ensures pro-rata distribution
        amount1 = liquidity.mul(balance1) / _totalSupply; // using balances ensures pro-rata distribution
        require(
            amount0 > 0 &amp;&amp; amount1 > 0,
            "Konoha: INSUFFICIENT_LIQUIDITY_BURNED"
        );
        _burn(address(this), liquidity);
        _safeTransfer(_token0, to, amount0);
        _safeTransfer(_token1, to, amount1);
        balance0 = IERC20(_token0).balanceOf(address(this));
        balance1 = IERC20(_token1).balanceOf(address(this));

        _update(balance0, balance1, _reserve0, _reserve1);
        if (feeOn) kLast = uint256(reserve0).mul(reserve1); // reserve0 and reserve1 are up-to-date
        emit Burn(msg.sender, amount0, amount1, to);
    &#125;

    // this low-level function should be called from a contract which performs important safety checks
    function swap(
        uint256 amount0Out,
        uint256 amount1Out,
        address to,
        bytes calldata data
    ) external lock &#123;
        require(
            amount0Out > 0 || amount1Out > 0,
            "Konoha: INSUFFICIENT_OUTPUT_AMOUNT"
        );

        uint256 balance0;
        uint256 balance1;
        &#123;
            // scope for _token&#123;0,1&#125;, avoids stack too deep errors
            address _token0 = token0;
            address _token1 = token1;
            require(to != _token0 &amp;&amp; to != _token1, "Konoha: INVALID_TO");
            if (amount0Out > 0) _safeTransfer(_token0, to, amount0Out); // optimistically transfer tokens
            if (amount1Out > 0) _safeTransfer(_token1, to, amount1Out); // optimistically transfer tokens
            if (data.length > 0)
                IKonohaCallee(to).KonohaCall(
                    msg.sender,
                    amount0Out,
                    amount1Out,
                    data
                );
            balance0 = IERC20(_token0).balanceOf(address(this));
            balance1 = IERC20(_token1).balanceOf(address(this));
        &#125;

        (uint112 _reserve0, uint112 _reserve1, ) = getReserves(); // gas savings
        require(
            amount0Out &lt; _reserve0 &amp;&amp; amount1Out &lt; _reserve1,
            "Konoha: INSUFFICIENT_LIQUIDITY"
        );

        uint256 amount0In = balance0 > _reserve0 - amount0Out
            ? balance0 - (_reserve0 - amount0Out)
            : 0;//1e18
        uint256 amount1In = balance1 > _reserve1 - amount1Out
            ? balance1 - (_reserve1 - amount1Out)
            : 0;//0
        require(
            amount0In > 0 || amount1In > 0,
            "Konoha: INSUFFICIENT_INPUT_AMOUNT"
        );
        &#123;
            // scope for reserve&#123;0,1&#125;Adjusted, avoids stack too deep errors
            uint256 balance0Adjusted = balance0.mul(1000).sub(
                amount0In.mul(25)
            );
            uint256 balance1Adjusted = balance1.mul(1000).sub(
                amount1In.mul(25)
            );
            require(
                balance0Adjusted.mul(balance1Adjusted) >=
                    uint256(_reserve0).mul(_reserve1).mul(1000**2),
                "Konoha: K"
            );
        &#125;

        _update(balance0, balance1, _reserve0, _reserve1);
        emit Swap(msg.sender, amount0In, amount1In, amount0Out, amount1Out, to);
    &#125;

    // force balances to match reserves
    function skim(address to) external lock &#123;
        address _token0 = token0; // gas savings
        address _token1 = token1; // gas savings
        _safeTransfer(
            _token0,
            to,
            IERC20(_token0).balanceOf(address(this)).sub(reserve0)
        );
        _safeTransfer(
            _token1,
            to,
            IERC20(_token1).balanceOf(address(this)).sub(reserve1)
        );
    &#125;

    // force reserves to match balances
    function sync() external &#123;
        _update(
            IERC20(token0).balanceOf(address(this)),
            IERC20(token1).balanceOf(address(this)),
            reserve0,
            reserve1
        );
    &#125;
&#125;

contract KonohaFactory is IKonohaFactory &#123;
    address public feeTo;
    address public feeToSetter;

    mapping(address => mapping(address => address)) public getPair;
    address[] public allPairs;

    event PairCreated(
        address indexed token0,
        address indexed token1,
        address pair,
        uint256
    );

    constructor(address _feeToSetter) public &#123;
        feeToSetter = _feeToSetter;
    &#125;

    function allPairsLength() external view returns (uint256) &#123;
        return allPairs.length;
    &#125;

    function createPair(address tokenA, address tokenB)
        external
        returns (address pair)
    &#123;
        require(tokenA != tokenB, "Konoha: IDENTICAL_ADDRESSES");
        (address token0, address token1) = tokenA &lt; tokenB
            ? (tokenA, tokenB)
            : (tokenB, tokenA);
        require(token0 != address(0), "Konoha: ZERO_ADDRESS");
        require(getPair[token0][token1] == address(0), "Konoha: PAIR_EXISTS"); // single check is sufficient
        bytes memory bytecode = type(KonohaPair).creationCode;
        bytes32 salt = keccak256(abi.encodePacked(token0, token1));
        assembly &#123;
            pair := create2(0, add(bytecode, 32), mload(bytecode), salt)
        &#125;
        IKonohaPair(pair).initialize(token0, token1);
        getPair[token0][token1] = pair;
        getPair[token1][token0] = pair; // populate mapping in the reverse direction
        allPairs.push(pair);
        emit PairCreated(token0, token1, pair, allPairs.length);
    &#125;

    function setFeeTo(address _feeTo) external &#123;
        require(msg.sender == feeToSetter, "Konoha: FORBIDDEN");
        feeTo = _feeTo;
    &#125;

    function setFeeToSetter(address _feeToSetter) external &#123;
        require(msg.sender == feeToSetter, "Konoha: FORBIDDEN");
        feeToSetter = _feeToSetter;
    &#125;

    function test() public view returns (bytes32) &#123;
        return
            keccak256(
                "Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)"
            );
    &#125;
&#125;

// a library for performing overflow-safe math, courtesy of DappHub (https://github.com/dapphub/ds-math)

library SafeMath &#123;
    function add(uint256 x, uint256 y) internal pure returns (uint256 z) &#123;
        require((z = x + y) >= x, "ds-math-add-overflow");
    &#125;

    function sub(uint256 x, uint256 y) internal pure returns (uint256 z) &#123;
        require((z = x - y) &lt;= x, "ds-math-sub-underflow");
    &#125;

    function mul(uint256 x, uint256 y) internal pure returns (uint256 z) &#123;
        require(y == 0 || (z = x * y) / y == x, "ds-math-mul-overflow");
    &#125;
&#125;

// a library for performing various math operations

library Math &#123;
    function min(uint256 x, uint256 y) internal pure returns (uint256 z) &#123;
        z = x &lt; y ? x : y;
    &#125;

    // babylonian method (https://en.wikipedia.org/wiki/Methods_of_computing_square_roots#Babylonian_method)
    function sqrt(uint256 y) internal pure returns (uint256 z) &#123;
        if (y > 3) &#123;
            z = y;
            uint256 x = y / 2 + 1;
            while (x &lt; z) &#123;
                z = x;
                x = (y / x + x) / 2;
            &#125;
        &#125; else if (y != 0) &#123;
            z = 1;
        &#125;
    &#125;
&#125;

// a library for handling binary fixed point numbers (https://en.wikipedia.org/wiki/Q_(number_format))

// range: [0, 2**112 - 1]
// resolution: 1 / 2**112

library UQ112x112 &#123;
    uint224 constant Q112 = 2**112;

    // encode a uint112 as a UQ112x112
    function encode(uint112 y) internal pure returns (uint224 z) &#123;
        z = uint224(y) * Q112; // never overflows
    &#125;

    // divide a UQ112x112 by a uint112, returning a UQ112x112
    function uqdiv(uint224 x, uint112 y) internal pure returns (uint224 z) &#123;
        z = x / uint224(y);
    &#125;
&#125;




