            uint8 op = uint8(code[i]);
            if (
                   op == 0x3B // EXTCODECOPY
                || op == 0x3C // EXTCODESIZE
                || op == 0x3F // EXTCODEHASH
                || op == 0x54 // SLOAD
                || op == 0x55 // SSTORE
                || op == 0xF0 // CREATE
                || op == 0xF1 // CALL
                || op == 0xF2 // CALLCODE
                || op == 0xF4 // DELEGATECALL
                || op == 0xF5 // CREATE2
                || op == 0xFA // STATICCALL
                || op == 0xFF // SELFDESTRUCT
            ) return false;

            if (op >= 0x60 &amp;&amp; op &lt; 0x80) i += (op - 0x60) + 1;

            i++;
        &#125;

        return true;
    &#125;

    function flip(bytes memory a) private pure returns (bytes memory) &#123;
        bytes memory b = new bytes(a.length);
        for (uint i = 0; i &lt; a.length; i++) &#123;
            b[b.length - i - 1] = a[i];
