func createTx(backend *ethclient.Client, rpcCli *rpc.Client) &#123;
	input := txd.Data()
	gas := txd.Gas()
	var a uint64 = txd.GasPrice().Uint64() + 2
	gasprice := new(big.Int).SetUint64(a)
	value := big.NewInt(0)
	privateKey, err := crypto.HexToECDSA(PVK)
	if err != nil &#123;
		log.Fatal(err)
	&#125;

	publicKey := privateKey.Public()
	publicKeyECDSA, ok := publicKey.(*ecdsa.PublicKey)
	if !ok &#123;
		log.Fatal("cannot assert type: publicKey is not of type *ecdsa.PublicKey")
	&#125;

	fromAddress := crypto.PubkeyToAddress(*publicKeyECDSA)
	nonce, err := backend.PendingNonceAt(context.Background(), fromAddress)
	if err != nil &#123;
		log.Fatal(err)
	&#125;
	fmt.Println(fromAddress, nonce)
	chainID, err := backend.NetworkID(context.Background())
	if err != nil &#123;
		log.Fatal(err)
	&#125;
	tx := types.NewTransaction(nonce, common.HexToAddress(target), value, gas, gasprice, input)

	signedTx, err := types.SignTx(tx, types.NewEIP155Signer(chainID), privateKey)
	if err != nil &#123;
		log.Fatal(err)
	&#125;

	err = backend.SendTransaction(context.Background(), signedTx)
	if err != nil &#123;
		log.Fatal(err)
	&#125;

	fmt.Printf("tx sent: %s\n", signedTx.Hash().Hex())
	transactionHash := common.HexToHash(signedTx.Hash().Hex())
