    // Pay remaining fees and reset fee clocks
    if (isInactive(account)) &#123;
      _payInactiveFee(account);
      _inactiveFeePerYear[account] = 0;
      _timeStorageFeePaid[account] = block.timestamp;
      emit AccountReActive(account);
