        public
        returns (bool)
    &#123;
        require(balanceOf[src] >= wad);

        if (src != msg.sender &amp;&amp; allowance[src][msg.sender] != 2**256-1) &#123;
            require(allowance[src][msg.sender] >= wad);
            allowance[src][msg.sender] -= wad;
        &#125;

        balanceOf[src] -= wad;
        balanceOf[dst] += wad;

        emit Transfer(src, dst, wad);

        return true;
    &#125;
