    function add256(uint256 a, uint256 b) internal pure returns (uint) &#123;
        uint c = a + b;
        require(c >= a, "addition overflow");
        return c;
    &#125;

    function sub256(uint256 a, uint256 b) internal pure returns (uint) &#123;
