contract GovernorBravoDelegator is GovernorBravoDelegatorStorage, GovernorBravoEvents &#123;
	constructor(
			address timelock_,
			address uni_,
			address admin_,
	        address implementation_,
	        uint votingPeriod_,
	        uint votingDelay_,
            uint proposalThreshold_) public &#123;

        // Admin set to msg.sender for initialization
        admin = msg.sender;

        delegateTo(implementation_, abi.encodeWithSignature("initialize(address,address,uint256,uint256,uint256)",
                                                            timelock_,
                                                            uni_,
                                                            votingPeriod_,
                                                            votingDelay_,
